// Extract request data
const inputData = $input.item.json;

// Get symbol and timeframe from request
const symbol = inputData.symbol || 'EURUSD';
const timeframe = inputData.timeframe || '1h';

return {
  json: {
    symbol,
    timeframe,
    requestId: inputData.requestId || Date.now().toString(),
    userId: inputData.userId || 'anonymous',
    timestamp: new Date().toISOString()
  }
};

// Process the raw forex data for SNR analysis
const rawData = $input.item.json;
const symbol = $input.item.json.symbol || $json.symbol;
const timeframe = $input.item.json.timeframe || $json.timeframe;

// Mock data structure if API is not available
let processedData;
if (!Array.isArray(rawData) || rawData.length === 0) {
  // Generate mock data for testing
  processedData = [];
  const basePrice = symbol === 'XAUUSD' ? 2000 : 1.1;
  const now = new Date();

  for (let i = 0; i < 200; i++) {
    const timestamp = new Date(now.getTime() - (i * getTimeframeMinutes(timeframe) * 60 * 1000));
    const volatility = symbol === 'XAUUSD' ? 5 : 0.0005;
    const open = basePrice + (Math.random() - 0.5) * volatility * 10;
    const high = open + Math.random() * volatility;
    const low = open - Math.random() * volatility;
    const close = low + Math.random() * (high - low);
    const volume = Math.floor(Math.random() * 1000) + 500;

    processedData.unshift({
      timestamp: timestamp.toISOString(),
      open,
      high,
      low,
      close,
      volume
    });
  }
} else {
  // Process actual API data
  processedData = rawData.map(candle => ({
    timestamp: new Date(candle.timestamp).toISOString(),
    open: parseFloat(candle.open),
    high: parseFloat(candle.high),
    low: parseFloat(candle.low),
    close: parseFloat(candle.close),
    volume: parseFloat(candle.volume || 0)
  }));
}

// Helper function to convert timeframe to minutes
function getTimeframeMinutes(timeframe) {
  switch(timeframe) {
    case '1m': return 1;
    case '5m': return 5;
    case '15m': return 15;
    case '30m': return 30;
    case '1h': return 60;
    case '4h': return 240;
    case 'D': return 1440;
    default: return 15;
  }
}

// Advanced SNR Analysis Functions

// 1. Identify swing highs and lows
function findSwings(data, lookback = 5) {
  const swingHighs = [];
  const swingLows = [];

  // Skip first and last few candles based on lookback
  for (let i = lookback; i < data.length - lookback; i++) {
    // Check for swing high
    let isSwingHigh = true;
    for (let j = i - lookback; j < i; j++) {
      if (data[j].high > data[i].high) {
        isSwingHigh = false;
        break;
      }
    }
    for (let j = i + 1; j <= i + lookback; j++) {
      if (data[j].high > data[i].high) {
        isSwingHigh = false;
        break;
      }
    }

    if (isSwingHigh) {
      swingHighs.push({
        price: data[i].high,
        index: i,
        timestamp: data[i].timestamp
      });
    }

    // Check for swing low
    let isSwingLow = true;
    for (let j = i - lookback; j < i; j++) {
      if (data[j].low < data[i].low) {
        isSwingLow = false;
        break;
      }
    }
    for (let j = i + 1; j <= i + lookback; j++) {
      if (data[j].low < data[i].low) {
        isSwingLow = false;
        break;
      }
    }

    if (isSwingLow) {
      swingLows.push({
        price: data[i].low,
        index: i,
        timestamp: data[i].timestamp
      });
    }
  }

  return { swingHighs, swingLows };
}

// 2. Identify support and resistance zones
function identifySNRZones(data, swings, threshold = 0.001) {
  const { swingHighs, swingLows } = swings;

  // Adjust threshold based on symbol (higher for gold)
  const priceThreshold = symbol === 'XAUUSD' ? threshold * 10 : threshold;

  // Group swing highs into resistance zones
  const resistanceZones = [];
  for (let i = 0; i < swingHighs.length; i++) {
    const currentHigh = swingHighs[i].price;
    let zoneFound = false;

    // Check if this swing high belongs to an existing zone
    for (let j = 0; j < resistanceZones.length; j++) {
      const zone = resistanceZones[j];
      const zoneMidpoint = (zone.upper + zone.lower) / 2;

      // If price is within threshold of zone midpoint, add to zone
      if (Math.abs(currentHigh - zoneMidpoint) / zoneMidpoint < priceThreshold) {
        // Update zone boundaries if needed
        if (currentHigh > zone.upper) {
          zone.upper = currentHigh;
        } else if (currentHigh < zone.lower) {
          zone.lower = currentHigh;
        }

        // Add this swing to the zone's touches
        zone.touches.push({
          price: currentHigh,
          timestamp: swingHighs[i].timestamp
        });

        zoneFound = true;
        break;
      }
    }

    // If no matching zone found, create a new one
    if (!zoneFound) {
      resistanceZones.push({
        upper: currentHigh * (1 + priceThreshold/2),
        lower: currentHigh * (1 - priceThreshold/2),
        midpoint: currentHigh,
        touches: [{
          price: currentHigh,
          timestamp: swingHighs[i].timestamp
        }],
        strength: 1 // Initial strength
      });
    }
  }

  // Group swing lows into support zones
  const supportZones = [];
  for (let i = 0; i < swingLows.length; i++) {
    const currentLow = swingLows[i].price;
    let zoneFound = false;

    // Check if this swing low belongs to an existing zone
    for (let j = 0; j < supportZones.length; j++) {
      const zone = supportZones[j];
      const zoneMidpoint = (zone.upper + zone.lower) / 2;

      // If price is within threshold of zone midpoint, add to zone
      if (Math.abs(currentLow - zoneMidpoint) / zoneMidpoint < priceThreshold) {
        // Update zone boundaries if needed
        if (currentLow < zone.lower) {
          zone.lower = currentLow;
        } else if (currentLow > zone.upper) {
          zone.upper = currentLow;
        }

        // Add this swing to the zone's touches
        zone.touches.push({
          price: currentLow,
          timestamp: swingLows[i].timestamp
        });

        zoneFound = true;
        break;
      }
    }

    // If no matching zone found, create a new one
    if (!zoneFound) {
      supportZones.push({
        upper: currentLow * (1 + priceThreshold/2),
        lower: currentLow * (1 - priceThreshold/2),
        midpoint: currentLow,
        touches: [{
          price: currentLow,
          timestamp: swingLows[i].timestamp
        }],
        strength: 1 // Initial strength
      });
    }
  }

  // Calculate zone strength based on number of touches and recency
  const now = new Date();

  // Process resistance zones
  resistanceZones.forEach(zone => {
    // More touches = stronger zone
    zone.strength = zone.touches.length;

    // Calculate recency factor (more recent touches increase strength)
    let recencyScore = 0;
    zone.touches.forEach(touch => {
      const touchTime = new Date(touch.timestamp);
      const hoursSince = (now - touchTime) / (1000 * 60 * 60);
      // Touches within last 24 hours are weighted more
      if (hoursSince < 24) {
        recencyScore += 0.5;
      } else if (hoursSince < 72) {
        recencyScore += 0.3;
      } else {
        recencyScore += 0.1;
      }
    });

    zone.strength += recencyScore;
    zone.midpoint = (zone.upper + zone.lower) / 2;
  });

  // Process support zones
  supportZones.forEach(zone => {
    // More touches = stronger zone
    zone.strength = zone.touches.length;

    // Calculate recency factor (more recent touches increase strength)
    let recencyScore = 0;
    zone.touches.forEach(touch => {
      const touchTime = new Date(touch.timestamp);
      const hoursSince = (now - touchTime) / (1000 * 60 * 60);
      // Touches within last 24 hours are weighted more
      if (hoursSince < 24) {
        recencyScore += 0.5;
      } else if (hoursSince < 72) {
        recencyScore += 0.3;
      } else {
        recencyScore += 0.1;
      }
    });

    zone.strength += recencyScore;
    zone.midpoint = (zone.upper + zone.lower) / 2;
  });

  // Sort zones by strength (descending)
  resistanceZones.sort((a, b) => b.strength - a.strength);
  supportZones.sort((a, b) => b.strength - a.strength);

  return { resistanceZones, supportZones };
}

// 3. Identify market structure
function identifyMarketStructure(data, swings) {
  const { swingHighs, swingLows } = swings;

  // Need at least 2 swing highs and 2 swing lows to determine structure
  if (swingHighs.length < 2 || swingLows.length < 2) {
    return {
      trend: 'undefined',
      structure: 'insufficient data'
    };
  }

  // Sort swings by index (chronological order)
  swingHighs.sort((a, b) => a.index - b.index);
  swingLows.sort((a, b) => a.index - b.index);

  // Check for higher highs and higher lows (uptrend)
  const higherHighs = swingHighs[swingHighs.length - 1].price > swingHighs[swingHighs.length - 2].price;
  const higherLows = swingLows[swingLows.length - 1].price > swingLows[swingLows.length - 2].price;

  // Check for lower highs and lower lows (downtrend)
  const lowerHighs = swingHighs[swingHighs.length - 1].price < swingHighs[swingHighs.length - 2].price;
  const lowerLows = swingLows[swingLows.length - 1].price < swingLows[swingLows.length - 2].price;

  // Determine trend
  let trend, structure;

  if (higherHighs && higherLows) {
    trend = 'uptrend';
    structure = 'strong uptrend';
  } else if (lowerHighs && lowerLows) {
    trend = 'downtrend';
    structure = 'strong downtrend';
  } else if (higherHighs && lowerLows) {
    trend = 'uptrend';
    structure = 'weak uptrend';
  } else if (lowerHighs && higherLows) {
    trend = 'downtrend';
    structure = 'weak downtrend';
  } else if (higherLows) {
    trend = 'uptrend';
    structure = 'potential uptrend';
  } else if (lowerHighs) {
    trend = 'downtrend';
    structure = 'potential downtrend';
  } else {
    trend = 'ranging';
    structure = 'consolidation';
  }

  // Check for potential trend reversal
  let potentialReversal = false;
  let reversalType = null;

  if (trend === 'uptrend' && lowerHighs) {
    potentialReversal = true;
    reversalType = 'bearish';
  } else if (trend === 'downtrend' && higherLows) {
    potentialReversal = true;
    reversalType = 'bullish';
  }

  return {
    trend,
    structure,
    potentialReversal,
    reversalType
  };
}

// 4. Identify key price levels
function identifyKeyLevels(data, zones) {
  const { resistanceZones, supportZones } = zones;
  const currentPrice = data[data.length - 1].close;

  // Get top 5 strongest zones
  const topResistance = resistanceZones.slice(0, 5);
  const topSupport = supportZones.slice(0, 5);

  // Find nearest resistance and support
  let nearestResistance = null;
  let nearestSupport = null;
  let minResistanceDist = Infinity;
  let minSupportDist = Infinity;

  topResistance.forEach(zone => {
    if (zone.midpoint > currentPrice) {
      const distance = zone.midpoint - currentPrice;
      if (distance < minResistanceDist) {
        minResistanceDist = distance;
        nearestResistance = zone;
      }
    }
  });

  topSupport.forEach(zone => {
    if (zone.midpoint < currentPrice) {
      const distance = currentPrice - zone.midpoint;
      if (distance < minSupportDist) {
        minSupportDist = distance;
        nearestSupport = zone;
      }
    }
  });

  // Calculate distance to nearest levels as percentage
  const resistanceDistance = nearestResistance ? 
    ((nearestResistance.midpoint - currentPrice) / currentPrice) * 100 : null;

  const supportDistance = nearestSupport ? 
    ((currentPrice - nearestSupport.midpoint) / currentPrice) * 100 : null;

  return {
    nearestResistance,
    nearestSupport,
    resistanceDistance,
    supportDistance,
    topResistance,
    topSupport
  };
}

// 5. Identify potential trade setups
function identifyTradeSetups(data, structure, keyLevels) {
  const currentPrice = data[data.length - 1].close;
  const { trend, potentialReversal, reversalType } = structure;
  const { nearestResistance, nearestSupport, resistanceDistance, supportDistance } = keyLevels;

  const setups = [];

  // 1. Trend continuation setup
  if (trend === 'uptrend' && !potentialReversal && nearestSupport) {
    setups.push({
      type: 'trend_continuation',
      direction: 'buy',
      entry: nearestSupport.midpoint,
      stopLoss: nearestSupport.lower * 0.998, // Just below support zone
      takeProfit: nearestResistance ? nearestResistance.midpoint : currentPrice * 1.01,
      description: 'Buy at support in uptrend',
      confidence: supportDistance < 0.5 ? 'high' : 'medium' // Higher confidence if price is close to support
    });
  } else if (trend === 'downtrend' && !potentialReversal && nearestResistance) {
    setups.push({
      type: 'trend_continuation',
      direction: 'sell',
      entry: nearestResistance.midpoint,
      stopLoss: nearestResistance.upper * 1.002, // Just above resistance zone
      takeProfit: nearestSupport ? nearestSupport.midpoint : currentPrice * 0.99,
      description: 'Sell at resistance in downtrend',
      confidence: resistanceDistance < 0.5 ? 'high' : 'medium' // Higher confidence if price is close to resistance
    });
  }

  // 2. Breakout setup
  if (nearestResistance && resistanceDistance < 0.2) {
    setups.push({
      type: 'breakout',
      direction: 'buy',
      entry: nearestResistance.upper,
      stopLoss: nearestResistance.lower,
      takeProfit: nearestResistance.upper + (nearestResistance.upper - nearestResistance.lower) * 2,
      description: 'Buy breakout above resistance',
      confidence: trend === 'uptrend' ? 'high' : 'medium'
    });
  }

  if (nearestSupport && supportDistance < 0.2) {
    setups.push({
      type: 'breakout',
      direction: 'sell',
      entry: nearestSupport.lower,
      stopLoss: nearestSupport.upper,
      takeProfit: nearestSupport.lower - (nearestSupport.upper - nearestSupport.lower) * 2,
      description: 'Sell breakout below support',
      confidence: trend === 'downtrend' ? 'high' : 'medium'
    });
  }

  // 3. Reversal setup
  if (potentialReversal) {
    if (reversalType === 'bullish' && nearestSupport) {
      setups.push({
        type: 'reversal',
        direction: 'buy',
        entry: currentPrice,
        stopLoss: nearestSupport.lower * 0.998,
        takeProfit: currentPrice + (currentPrice - nearestSupport.lower) * 2,
        description: 'Potential bullish reversal',
        confidence: 'medium'
      });
    } else if (reversalType === 'bearish' && nearestResistance) {
      setups.push({
        type: 'reversal',
        direction: 'sell',
        entry: currentPrice,
        stopLoss: nearestResistance.upper * 1.002,
        takeProfit: currentPrice - (nearestResistance.upper - currentPrice) * 2,
        description: 'Potential bearish reversal',
        confidence: 'medium'
      });
    }
  }

  // Sort setups by confidence
  const confidenceMap = { 'high': 3, 'medium': 2, 'low': 1 };
  setups.sort((a, b) => confidenceMap[b.confidence] - confidenceMap[a.confidence]);

  return setups;
}

// Perform SNR analysis
const swings = findSwings(processedData);
const snrZones = identifySNRZones(processedData, swings);
const marketStructure = identifyMarketStructure(processedData, swings);
const keyLevels = identifyKeyLevels(processedData, snrZones);
const tradeSetups = identifyTradeSetups(processedData, marketStructure, keyLevels);

// Current price
const currentPrice = processedData[processedData.length - 1].close;

// Prepare analysis data
const snrAnalysis = {
  symbol,
  timeframe,
  currentPrice,
  timestamp: processedData[processedData.length - 1].timestamp,
  marketStructure,
  keyLevels: {
    resistance: keyLevels.topResistance.map(zone => ({
      level: zone.midpoint,
      strength: zone.strength,
      touches: zone.touches.length
    })),
    support: keyLevels.topSupport.map(zone => ({
      level: zone.midpoint,
      strength: zone.strength,
      touches: zone.touches.length
    }))
  },
  nearestLevels: {
    resistance: keyLevels.nearestResistance ? {
      level: keyLevels.nearestResistance.midpoint,
      distance: keyLevels.resistanceDistance
    } : null,
    support: keyLevels.nearestSupport ? {
      level: keyLevels.nearestSupport.midpoint,
      distance: keyLevels.supportDistance
    } : null
  },
  tradeSetups
};

return {json: snrAnalysis};

