// Helper function to generate mock forex data
function generateMockForexData(symbol, timeframe, count) {
  const candles = [];
  const now = new Date();
  const basePrice = symbol === 'XAUUSD' ? 2000 : (symbol.includes('JPY') ? 150 : 1.1);
  const volatility = symbol === 'XAUUSD' ? 5 : (symbol.includes('JPY') ? 0.5 : 0.0005);
  let currentPrice = basePrice;

  // Generate candles in reverse order (oldest first)
  for (let i = count - 1; i >= 0; i--) {
    const minutesAgo = i * getTimeframeMinutes(timeframe);
    const timestamp = new Date(now.getTime() - minutesAgo * 60 * 1000);

    // Add some randomness but maintain a trend
    const trendBias = Math.sin(i / 20) * volatility * 0.5; // Create cyclic trends
    const randomChange = (Math.random() - 0.5) * volatility + trendBias;
    currentPrice += randomChange;

    const open = currentPrice;
    const high = open + Math.random() * volatility;
    const low = open - Math.random() * volatility;
    const close = low + Math.random() * (high - low);
    const volume = Math.floor(Math.random() * 1000) + 500;

    candles.push({
      timestamp: timestamp.toISOString(),
      open,
      high,
      low,
      close,
      volume
    });
  }

  return candles;
}

// Helper function to convert timeframe to minutes
