// RSI calculation
function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) {
    return 50; // Default value if not enough data
  }

  let gains = 0;
  let losses = 0;

  // Calculate initial gains and losses
  for (let i = 1; i <= period; i++) {
    const change = prices[i] - prices[i-1];
    if (change >= 0) {
      gains += change;
    } else {
      losses -= change;
    }
  }

  let avgGain = gains / period;
  let avgLoss = losses / period;

  // Calculate RSI using the smoothed method
  for (let i = period + 1; i < prices.length; i++) {
    const change = prices[i] - prices[i-1];
    if (change >= 0) {
      avgGain = (avgGain * (period - 1) + change) / period;
      avgLoss = (avgLoss * (period - 1)) / period;
    } else {
      avgGain = (avgGain * (period - 1)) / period;
      avgLoss = (avgLoss * (period - 1) - change) / period;
    }
  }

  if (avgLoss === 0) {
    return 100;
  }

  const RS = avgGain / avgLoss;
  return 100 - (100 / (1 + RS));
}

// SMA calculation
function calculateSMA(prices, period) {
  if (prices.length < period) {
    return null;
  }

  let sum = 0;
  for (let i = prices.length - period; i < prices.length; i++) {
    sum += prices[i];
  }
  return sum / period;
}

// EMA calculation
function calculateEMA(prices, period) {
  if (prices.length < period) {
    return null;
  }

  let ema = calculateSMA(prices.slice(0, period), period);
  const multiplier = 2 / (period + 1);

  for (let i = period; i < prices.length; i++) {
    ema = (prices[i] - ema) * multiplier + ema;
  }

  return ema;
}

// MACD calculation
function calculateMACD(prices) {
  const ema12 = calculateEMA(prices, 12);
  const ema26 = calculateEMA(prices, 26);

  if (ema12 === null || ema26 === null) {
    return { macdLine: 0, signalLine: 0, histogram: 0 };
  }

  const macdLine = ema12 - ema26;
  const signalLine = calculateEMA([...prices.slice(0, prices.length - 9), macdLine], 9);
  const histogram = macdLine - signalLine;

  return { macdLine, signalLine, histogram };
}

// Bollinger Bands calculation
function calculateBollingerBands(prices, period = 20, stdDev = 2) {
  if (prices.length < period) {
    return { upper: null, middle: null, lower: null };
  }

  const sma = calculateSMA(prices, period);

  // Calculate standard deviation
  let sum = 0;
  for (let i = prices.length - period; i < prices.length; i++) {
    sum += Math.pow(prices[i] - sma, 2);
  }
  const std = Math.sqrt(sum / period);

  return {
    upper: sma + (stdDev * std),
    middle: sma,
    lower: sma - (stdDev * std)
  };
}

// ATR calculation
function calculateATR(candles, period = 14) {
  if (candles.length < period + 1) {
    return 0;
  }

  const trueRanges = [];

  // Calculate true ranges
  for (let i = 1; i < candles.length; i++) {
    const high = candles[i].high;
    const low = candles[i].low;
    const prevClose = candles[i-1].close;

    const tr1 = high - low;
    const tr2 = Math.abs(high - prevClose);
    const tr3 = Math.abs(low - prevClose);

    const trueRange = Math.max(tr1, tr2, tr3);
    trueRanges.push(trueRange);
  }

  // Calculate ATR as simple average of true ranges
  const recentTR = trueRanges.slice(-period);
  const atr = recentTR.reduce((sum, tr) => sum + tr, 0) / period;

  return atr;
}

// Stochastic Oscillator calculation
function calculateStochastic(closes, highs, lows, kPeriod = 14, dPeriod = 3) {
  if (closes.length < kPeriod) {
    return { k: 50, d: 50 };
  }

  // Calculate %K
  const recentCloses = closes.slice(-kPeriod);
  const recentHighs = highs.slice(-kPeriod);
  const recentLows = lows.slice(-kPeriod);

  const highestHigh = Math.max(...recentHighs);
  const lowestLow = Math.min(...recentLows);

  const currentClose = closes[closes.length - 1];
  const k = ((currentClose - lowestLow) / (highestHigh - lowestLow)) * 100;

  // For simplicity, we'll approximate %D
  const d = k * 0.9; // Approximation

  return { k, d };
}

// Ichimoku Cloud calculation (simplified)
function calculateIchimoku(candles) {
  const highs = candles.map(c => c.high);
  const lows = candles.map(c => c.low);
  const closes = candles.map(c => c.close);

  // Tenkan-sen (Conversion Line): (9-period high + 9-period low)/2
  const tenkanSen = (Math.max(...highs.slice(-9)) + Math.min(...lows.slice(-9))) / 2;

  // Kijun-sen (Base Line): (26-period high + 26-period low)/2
  const kijunSen = (Math.max(...highs.slice(-26)) + Math.min(...lows.slice(-26))) / 2;

  // Senkou Span A (Leading Span A): (Tenkan-sen + Kijun-sen)/2
  const senkouSpanA = (tenkanSen + kijunSen) / 2;

  // Senkou Span B (Leading Span B): (52-period high + 52-period low)/2
  const senkouSpanB = (Math.max(...highs.slice(-52)) + Math.min(...lows.slice(-52))) / 2;

  // Chikou Span (Lagging Span): Current closing price time-shifted backwards 26 periods
  const chikouSpan = closes[closes.length - 1];

  return {
    tenkanSen,
    kijunSen,
    senkouSpanA,
    senkouSpanB,
    chikouSpan
  };
}

// Support and Resistance calculation
function calculateSupportResistance(candles) {
  const highs = candles.map(c => c.high);
  const lows = candles.map(c => c.low);

  // Find local maxima and minima
  const resistanceLevels = [];
  const supportLevels = [];

  // Look for patterns in recent candles (last 30)
  const recentCandles = candles.slice(-30);

  for (let i = 5; i < recentCandles.length - 5; i++) {
    // Check for resistance (local high)
    if (recentCandles[i].high > recentCandles[i-1].high && 
        recentCandles[i].high > recentCandles[i-2].high &&
        recentCandles[i].high > recentCandles[i+1].high && 
        recentCandles[i].high > recentCandles[i+2].high) {
      resistanceLevels.push(recentCandles[i].high);
    }

    // Check for support (local low)
    if (recentCandles[i].low < recentCandles[i-1].low && 
        recentCandles[i].low < recentCandles[i-2].low &&
        recentCandles[i].low < recentCandles[i+1].low && 
        recentCandles[i].low < recentCandles[i+2].low) {
      supportLevels.push(recentCandles[i].low);
    }
  }

  // Group similar levels (within 0.1%)
  const groupedResistance = groupSimilarLevels(resistanceLevels, 0.001);
  const groupedSupport = groupSimilarLevels(supportLevels, 0.001);

  return {
    resistance: groupedResistance.slice(0, 3), // Top 3 resistance levels
    support: groupedSupport.slice(0, 3) // Top 3 support levels
  };
}

// Helper function to group similar price levels
function groupSimilarLevels(levels, threshold) {
  if (levels.length === 0) return [];

  // Sort levels
  levels.sort((a, b) => a - b);

  const groups = [];
  let currentGroup = [levels[0]];

  for (let i = 1; i < levels.length; i++) {
    const currentLevel = levels[i];
    const prevLevel = levels[i-1];

    // Check if current level is within threshold of previous level
    if ((currentLevel - prevLevel) / prevLevel < threshold) {
      // Add to current group
      currentGroup.push(currentLevel);
    } else {
      // Create a new group
      groups.push(currentGroup);
      currentGroup = [currentLevel];
    }
  }

  // Add the last group
  if (currentGroup.length > 0) {
    groups.push(currentGroup);
  }

  // Calculate average value for each group
  return groups.map(group => {
    const sum = group.reduce((acc, val) => acc + val, 0);
    return sum / group.length;
  });
}

// Fibonacci levels calculation
function calculateFibonacciLevels(candles) {
  // Find recent swing high and low
  const recentCandles = candles.slice(-50);
  let swingHigh = -Infinity;
  let swingLow = Infinity;

  for (const candle of recentCandles) {
    if (candle.high > swingHigh) swingHigh = candle.high;
    if (candle.low < swingLow) swingLow = candle.low;
  }

  const range = swingHigh - swingLow;

  // Calculate Fibonacci retracement levels
  return {
    level0: swingLow, // 0%
    level236: swingLow + range * 0.236, // 23.6%
    level382: swingLow + range * 0.382, // 38.2%
    level50: swingLow + range * 0.5, // 50%
    level618: swingLow + range * 0.618, // 61.8%
    level786: swingLow + range * 0.786, // 78.6%
    level100: swingHigh // 100%
  };
}

// Volume Profile calculation (simplified)
function calculateVolumeProfile(candles) {
  // Divide the price range into 10 zones
  const prices = candles.map(c => c.close);
  const volumes = candles.map(c => c.volume);

  const minPrice = Math.min(...prices);
  const maxPrice = Math.max(...prices);
  const priceRange = maxPrice - minPrice;
  const zoneSize = priceRange / 10;

  // Initialize volume zones
  const volumeZones = Array(10).fill(0);

  // Assign volume to each zone
  for (let i = 0; i < prices.length; i++) {
    const zoneIndex = Math.min(9, Math.floor((prices[i] - minPrice) / zoneSize));
    volumeZones[zoneIndex] += volumes[i];
  }

  // Find the zone with highest volume (Point of Control)
  let maxVolumeIndex = 0;
  for (let i = 1; i < volumeZones.length; i++) {
    if (volumeZones[i] > volumeZones[maxVolumeIndex]) {
      maxVolumeIndex = i;
    }
  }

  const poc = minPrice + (maxVolumeIndex + 0.5) * zoneSize;

  return {
    volumeZones,
    pointOfControl: poc,
    valueArea: [poc * 0.95, poc * 1.05] // Simplified value area
  };
}

// Market Structure analysis
function analyzeMarketStructure(candles) {
  // Find swing highs and lows
  const swingHighs = [];
  const swingLows = [];

  // Look at recent candles
  const recentCandles = candles.slice(-30);

  for (let i = 5; i < recentCandles.length - 5; i++) {
    // Check for swing high
    if (recentCandles[i].high > recentCandles[i-1].high && 
        recentCandles[i].high > recentCandles[i-2].high &&
        recentCandles[i].high > recentCandles[i+1].high && 
        recentCandles[i].high > recentCandles[i+2].high) {
      swingHighs.push({
        price: recentCandles[i].high,
        index: i
      });
    }

    // Check for swing low
    if (recentCandles[i].low < recentCandles[i-1].low && 
        recentCandles[i].low < recentCandles[i-2].low &&
        recentCandles[i].low < recentCandles[i+1].low && 
        recentCandles[i].low < recentCandles[i+2].low) {
      swingLows.push({
        price: recentCandles[i].low,
        index: i
      });
    }
  }

  // Sort by index
  swingHighs.sort((a, b) => a.index - b.index);
  swingLows.sort((a, b) => a.index - b.index);

  // Check for higher highs and higher lows (uptrend)
  let higherHighs = false;
  let higherLows = false;

  if (swingHighs.length >= 2) {
    higherHighs = swingHighs[swingHighs.length - 1].price > swingHighs[swingHighs.length - 2].price;
  }

  if (swingLows.length >= 2) {
    higherLows = swingLows[swingLows.length - 1].price > swingLows[swingLows.length - 2].price;
  }

  // Check for lower highs and lower lows (downtrend)
  let lowerHighs = false;
  let lowerLows = false;

  if (swingHighs.length >= 2) {
    lowerHighs = swingHighs[swingHighs.length - 1].price < swingHighs[swingHighs.length - 2].price;
  }

  if (swingLows.length >= 2) {
    lowerLows = swingLows[swingLows.length - 1].price < swingLows[swingLows.length - 2].price;
  }

  // Determine trend
  let trend;
  if (higherHighs && higherLows) {
    trend = 'uptrend';
  } else if (lowerHighs && lowerLows) {
    trend = 'downtrend';
  } else {
    trend = 'ranging';
  }

  return {
    trend,
    swingHighs: swingHighs.map(sh => sh.price),
    swingLows: swingLows.map(sl => sl.price),
    higherHighs,
    higherLows,
    lowerHighs,
    lowerLows
  };
}

// Momentum calculation
function calculateMomentum(prices, period = 10) {
  if (prices.length < period) {
    return 0;
  }

  const currentPrice = prices[prices.length - 1];
  const pastPrice = prices[prices.length - period];

  return (currentPrice / pastPrice) * 100;
}

// Volatility calculation
function calculateVolatility(prices, period = 20) {
  if (prices.length < period) {
    return 0;
  }

  const returns = [];
  for (let i = 1; i < prices.length; i++) {
    returns.push((prices[i] - prices[i-1]) / prices[i-1]);
  }

  const recentReturns = returns.slice(-period);
  const meanReturn = recentReturns.reduce((sum, ret) => sum + ret, 0) / period;

  // Calculate standard deviation of returns
  const squaredDiffs = recentReturns.map(ret => Math.pow(ret - meanReturn, 2));
  const variance = squaredDiffs.reduce((sum, diff) => sum + diff, 0) / period;
  const stdDev = Math.sqrt(variance);

  // Annualized volatility (approximation)
  return stdDev * Math.sqrt(252) * 100; // Assuming 252 trading days in a year
}

// Chart pattern detection
function detectChartPatterns(candles) {
  const patterns = [];

  // Check for bullish engulfing
  for (let i = 1; i < candles.length; i++) {
    const current = candles[i];
    const previous = candles[i-1];

    // Bullish engulfing
    if (current.close > current.open && 
        previous.close < previous.open && 
        current.open < previous.close && 
        current.close > previous.open) {
      patterns.push({
        type: 'bullish_engulfing',
        index: i,
        confidence: 'high'
      });
    }

    // Bearish engulfing
    if (current.close < current.open && 
        previous.close > previous.open && 
        current.open > previous.close && 
        current.close < previous.open) {
      patterns.push({
        type: 'bearish_engulfing',
        index: i,
        confidence: 'high'
      });
    }

    // Doji
    if (Math.abs(current.open - current.close) / (current.high - current.low) < 0.1) {
      patterns.push({
        type: 'doji',
        index: i,
        confidence: 'medium'
      });
    }
  }

  // Return only the most recent patterns (last 5 candles)
  return patterns.filter(p => p.index >= candles.length - 5);
}

