// Process the raw market data into a format suitable for analysis
const rawData = $input.item.json;

// Format: [timestamp, open, high, low, close, volume, ...]
const processedData = rawData.map(candle => {
  return {
    timestamp: new Date(candle[0]).toISOString(),
    open: parseFloat(candle[1]),
    high: parseFloat(candle[2]),
    low: parseFloat(candle[3]),
    close: parseFloat(candle[4]),
    volume: parseFloat(candle[5])
  };
});

// Calculate basic technical indicators
const calculateRSI = (data, period = 14) => {
  let gains = 0;
  let losses = 0;

  // Initial RSI calculation
  for (let i = 1; i <= period; i++) {
    const change = data[i].close - data[i-1].close;
    if (change >= 0) {
      gains += change;
    } else {
      losses -= change;
    }
  }

  let avgGain = gains / period;
  let avgLoss = losses / period;

  // Calculate RSI for the remaining data
  const rsiValues = [];
  for (let i = period; i < data.length; i++) {
    if (avgLoss === 0) {
      rsiValues.push(100);
    } else {
      const RS = avgGain / avgLoss;
      rsiValues.push(100 - (100 / (1 + RS)));
    }

    // Update average gain and loss
    if (i < data.length - 1) {
      const change = data[i+1].close - data[i].close;
      if (change >= 0) {
        avgGain = (avgGain * (period - 1) + change) / period;
        avgLoss = (avgLoss * (period - 1)) / period;
      } else {
        avgGain = (avgGain * (period - 1)) / period;
        avgLoss = (avgLoss * (period - 1) - change) / period;
      }
    }
  }

  return rsiValues[rsiValues.length - 1];
};

// Calculate Moving Averages
const calculateSMA = (data, period) => {
  const prices = data.map(candle => candle.close);
  let sum = 0;
  for (let i = prices.length - period; i < prices.length; i++) {
    sum += prices[i];
  }
  return sum / period;
};

const calculateEMA = (data, period) => {
  const prices = data.map(candle => candle.close);
  const k = 2 / (period + 1);
  let ema = calculateSMA(data.slice(0, period), period);

  for (let i = period; i < prices.length; i++) {
    ema = prices[i] * k + ema * (1 - k);
  }

  return ema;
};

// Calculate MACD
const calculateMACD = (data) => {
  const ema12 = calculateEMA(data, 12);
  const ema26 = calculateEMA(data, 26);
  const macdLine = ema12 - ema26;
  const signalLine = calculateEMA([...data.slice(0, data.length - 9), {close: macdLine}], 9);
  const histogram = macdLine - signalLine;

  return {
    macdLine,
    signalLine,
    histogram
  };
};

// Calculate Bollinger Bands
const calculateBollingerBands = (data, period = 20, stdDev = 2) => {
  const prices = data.map(candle => candle.close);
  const sma = calculateSMA(data, period);

  // Calculate standard deviation
  let sum = 0;
  for (let i = prices.length - period; i < prices.length; i++) {
    sum += Math.pow(prices[i] - sma, 2);
  }
  const std = Math.sqrt(sum / period);

  return {
    middle: sma,
    upper: sma + (stdDev * std),
    lower: sma - (stdDev * std)
  };
};

// Calculate indicators
const rsi = calculateRSI(processedData);
const sma20 = calculateSMA(processedData, 20);
const ema20 = calculateEMA(processedData, 20);
const macd = calculateMACD(processedData);
const bollingerBands = calculateBollingerBands(processedData);

// Current price
const currentPrice = processedData[processedData.length - 1].close;

// Prepare analysis data
const technicalAnalysis = {
  symbol: "BTCUSDT",
  currentPrice,
  timestamp: processedData[processedData.length - 1].timestamp,
  indicators: {
    rsi,
    sma20,
    ema20,
    macd,
    bollingerBands
  },
  processedData: processedData.slice(-5) // Include only the last 5 candles for reference
};

return {json: technicalAnalysis};

