// Define the top 10 forex pairs and gold to monitor
const symbols = [
  'EURUSD', 'USDJPY', 'GBPUSD', 'AUDUSD', 'USDCAD',
  'USDCHF', 'NZDUSD', 'EURJPY', 'EURGBP', 'GBPJPY',
  'XAUUSD' // Gold
];

// Define timeframes to analyze
const timeframes = ['5m', '15m', '1h', '4h', 'D'];

// Create items for each symbol and timeframe combination
const items = [];

for (const symbol of symbols) {
  for (const timeframe of timeframes) {
    items.push({
      json: {
        symbol,
        timeframe,
        timestamp: new Date().toISOString(),
        requestId: Date.now().toString()
      }
    });
  }
}

return items;

// Process the raw forex data into a format suitable for analysis
const rawData = $input.item.json;
const symbol = $input.item.json.symbol || $json.symbol;
const timeframe = $input.item.json.timeframe || $json.timeframe;

// Mock data structure if API is not available
let processedData;
if (!Array.isArray(rawData) || rawData.length === 0) {
  // Generate mock data for testing
  processedData = [];
  const basePrice = symbol === 'XAUUSD' ? 2000 : 1.1;
  const now = new Date();

  for (let i = 0; i < 100; i++) {
    const timestamp = new Date(now.getTime() - (i * getTimeframeMinutes(timeframe) * 60 * 1000));
    const volatility = symbol === 'XAUUSD' ? 5 : 0.0005;
    const open = basePrice + (Math.random() - 0.5) * volatility * 10;
    const high = open + Math.random() * volatility;
    const low = open - Math.random() * volatility;
    const close = low + Math.random() * (high - low);
    const volume = Math.floor(Math.random() * 1000) + 500;

    processedData.unshift({
      timestamp: timestamp.toISOString(),
      open,
      high,
      low,
      close,
      volume
    });
  }
} else {
  // Process actual API data
  processedData = rawData.map(candle => ({
    timestamp: new Date(candle.timestamp).toISOString(),
    open: parseFloat(candle.open),
    high: parseFloat(candle.high),
    low: parseFloat(candle.low),
    close: parseFloat(candle.close),
    volume: parseFloat(candle.volume || 0)
  }));
}

// Helper function to convert timeframe to minutes
function getTimeframeMinutes(timeframe) {
  switch(timeframe) {
    case '1m': return 1;
    case '5m': return 5;
    case '15m': return 15;
    case '30m': return 30;
    case '1h': return 60;
    case '4h': return 240;
    case 'D': return 1440;
    default: return 15;
  }
}

// Calculate technical indicators

// 1. Calculate RSI
function calculateRSI(data, period = 14) {
  if (data.length < period + 1) {
    return 50; // Default value if not enough data
  }

  let gains = 0;
  let losses = 0;

  // Calculate initial gains and losses
  for (let i = 1; i <= period; i++) {
    const change = data[i].close - data[i-1].close;
    if (change >= 0) {
      gains += change;
    } else {
      losses -= change;
    }
  }

  let avgGain = gains / period;
  let avgLoss = losses / period;

  // Calculate RSI using the smoothed method
  for (let i = period + 1; i < data.length; i++) {
    const change = data[i].close - data[i-1].close;
    if (change >= 0) {
      avgGain = (avgGain * (period - 1) + change) / period;
      avgLoss = (avgLoss * (period - 1)) / period;
    } else {
      avgGain = (avgGain * (period - 1)) / period;
      avgLoss = (avgLoss * (period - 1) - change) / period;
    }
  }

  if (avgLoss === 0) {
    return 100;
  }

  const RS = avgGain / avgLoss;
  return 100 - (100 / (1 + RS));
}

// 2. Calculate Moving Averages
function calculateMA(data, period, type = 'sma') {
  if (data.length < period) {
    return null;
  }

  const prices = data.map(candle => candle.close);

  if (type === 'sma') {
    // Simple Moving Average
    let sum = 0;
    for (let i = prices.length - period; i < prices.length; i++) {
      sum += prices[i];
    }
    return sum / period;
  } else if (type === 'ema') {
    // Exponential Moving Average
    let sum = 0;
    for (let i = 0; i < period; i++) {
      sum += prices[prices.length - period + i];
    }
    let ema = sum / period;

    const multiplier = 2 / (period + 1);
    for (let i = prices.length - period + 1; i < prices.length; i++) {
      ema = (prices[i] - ema) * multiplier + ema;
    }
    return ema;
  }

  return null;
}

// 3. Calculate MACD
function calculateMACD(data) {
  const ema12 = calculateMA(data, 12, 'ema');
  const ema26 = calculateMA(data, 26, 'ema');

  if (ema12 === null || ema26 === null) {
    return { macdLine: 0, signalLine: 0, histogram: 0 };
  }

  const macdLine = ema12 - ema26;

  // Calculate signal line (9-day EMA of MACD line)
  // For simplicity, we'll use a simple approximation
  const signalLine = macdLine * 0.9; // Approximation
  const histogram = macdLine - signalLine;

  return { macdLine, signalLine, histogram };
}

// 4. Calculate Support and Resistance levels (SNR)
function calculateSNR(data, lookback = 20) {
  if (data.length < lookback) {
    return { support: [], resistance: [] };
  }

  const recentData = data.slice(-lookback);

  // Find local minima (support) and maxima (resistance)
  const support = [];
  const resistance = [];

  // Skip first and last candle
  for (let i = 1; i < recentData.length - 1; i++) {
    // Check for local minimum (support)
    if (recentData[i].low < recentData[i-1].low && recentData[i].low < recentData[i+1].low) {
      support.push(recentData[i].low);
    }

    // Check for local maximum (resistance)
    if (recentData[i].high > recentData[i-1].high && recentData[i].high > recentData[i+1].high) {
      resistance.push(recentData[i].high);
    }
  }

  // Cluster similar levels (within 0.1% for forex, 0.5% for gold)
  const clusterThreshold = symbol === 'XAUUSD' ? 0.005 : 0.001;

  const clusteredSupport = clusterLevels(support, clusterThreshold);
  const clusteredResistance = clusterLevels(resistance, clusterThreshold);

  return { 
    support: clusteredSupport.slice(0, 3), // Return top 3 support levels
    resistance: clusteredResistance.slice(0, 3) // Return top 3 resistance levels
  };
}

// Helper function to cluster price levels
function clusterLevels(levels, threshold) {
  if (levels.length === 0) return [];

  // Sort levels
  levels.sort((a, b) => a - b);

  const clusters = [];
  let currentCluster = [levels[0]];

  for (let i = 1; i < levels.length; i++) {
    const currentLevel = levels[i];
    const prevLevel = levels[i-1];

    // Check if current level is within threshold of previous level
    if ((currentLevel - prevLevel) / prevLevel < threshold) {
      // Add to current cluster
      currentCluster.push(currentLevel);
    } else {
      // Create a new cluster
      clusters.push(currentCluster);
      currentCluster = [currentLevel];
    }
  }

  // Add the last cluster
  if (currentCluster.length > 0) {
    clusters.push(currentCluster);
  }

  // Calculate average value for each cluster
  return clusters.map(cluster => {
    const sum = cluster.reduce((acc, val) => acc + val, 0);
    return sum / cluster.length;
  });
}

// 5. Calculate Bollinger Bands
function calculateBollingerBands(data, period = 20, stdDev = 2) {
  if (data.length < period) {
    return { upper: null, middle: null, lower: null };
  }

  const sma = calculateMA(data, period, 'sma');
  if (sma === null) return { upper: null, middle: null, lower: null };

  // Calculate standard deviation
  const prices = data.slice(-period).map(candle => candle.close);
  const squaredDiffs = prices.map(price => Math.pow(price - sma, 2));
  const variance = squaredDiffs.reduce((sum, val) => sum + val, 0) / period;
  const std = Math.sqrt(variance);

  return {
    upper: sma + (stdDev * std),
    middle: sma,
    lower: sma - (stdDev * std)
  };
}

// 6. Calculate Average True Range (ATR) for volatility
function calculateATR(data, period = 14) {
  if (data.length < period + 1) {
    return 0;
  }

  const trueRanges = [];

  // Calculate true ranges
  for (let i = 1; i < data.length; i++) {
    const high = data[i].high;
    const low = data[i].low;
    const prevClose = data[i-1].close;

    const tr1 = high - low;
    const tr2 = Math.abs(high - prevClose);
    const tr3 = Math.abs(low - prevClose);

    const trueRange = Math.max(tr1, tr2, tr3);
    trueRanges.push(trueRange);
  }

  // Calculate ATR as simple average of true ranges
  const recentTR = trueRanges.slice(-period);
  const atr = recentTR.reduce((sum, tr) => sum + tr, 0) / period;

  return atr;
}

// 7. Calculate price momentum
function calculateMomentum(data, period = 10) {
  if (data.length < period) {
    return 0;
  }

  const currentPrice = data[data.length - 1].close;
  const pastPrice = data[data.length - period].close;

  return (currentPrice / pastPrice) * 100;
}

// Calculate all indicators
const rsi = calculateRSI(processedData);
const sma50 = calculateMA(processedData, 50, 'sma');
const sma200 = calculateMA(processedData, 200, 'sma');
const ema20 = calculateMA(processedData, 20, 'ema');
const macd = calculateMACD(processedData);
const snr = calculateSNR(processedData);
const bollingerBands = calculateBollingerBands(processedData);
const atr = calculateATR(processedData);
const momentum = calculateMomentum(processedData);

// Current price
const currentPrice = processedData[processedData.length - 1].close;
const previousPrice = processedData[processedData.length - 2].close;
const priceChange = ((currentPrice - previousPrice) / previousPrice) * 100;

// Prepare analysis data
const technicalAnalysis = {
  symbol,
  timeframe,
  currentPrice,
  priceChange,
  timestamp: processedData[processedData.length - 1].timestamp,
  indicators: {
    rsi,
    sma50,
    sma200,
    ema20,
    macd,
    snr,
    bollingerBands,
    atr,
    momentum
  },
  recentCandles: processedData.slice(-5) // Include only the last 5 candles for reference
};

return {json: technicalAnalysis};

