console.log('Invalid signal generated:', $input.item);

return {
  json: {
    error: 'Invalid forex trading signal generated',
    timestamp: new Date().toISOString(),
    data: $input.item
  }
};

// Aggregate all signals to create a comprehensive market overview
const items = $input.all.map(item => item.json);

// Group by symbol
const symbolGroups = {};
items.forEach(item => {
  if (!item.symbol) return; // Skip invalid items

  if (!symbolGroups[item.symbol]) {
    symbolGroups[item.symbol] = [];
  }
  symbolGroups[item.symbol].push(item);
});

// Create summary for each symbol across timeframes
const symbolSummaries = {};
Object.keys(symbolGroups).forEach(symbol => {
  const signals = symbolGroups[symbol];

  // Count signal types
  const signalCounts = {
    'STRONG BUY': 0,
    'BUY': 0,
    'NEUTRAL': 0,
    'SELL': 0,
    'STRONG SELL': 0
  };

  signals.forEach(signal => {
    if (signal.signal && signalCounts[signal.signal] !== undefined) {
      signalCounts[signal.signal]++;
    }
  });

  // Determine overall bias
  const buySignals = signalCounts['STRONG BUY'] + signalCounts['BUY'];
  const sellSignals = signalCounts['STRONG SELL'] + signalCounts['SELL'];
  const neutralSignals = signalCounts['NEUTRAL'];

  let overallBias;
  if (buySignals > sellSignals && buySignals > neutralSignals) {
    overallBias = 'Bullish';
  } else if (sellSignals > buySignals && sellSignals > neutralSignals) {
    overallBias = 'Bearish';
  } else {
    overallBias = 'Neutral';
  }

  // Find the strongest signal (highest confidence)
  let strongestSignal = null;
  let highestConfidence = 0;

  signals.forEach(signal => {
    if (signal.confidence && signal.confidence > highestConfidence) {
      highestConfidence = signal.confidence;
      strongestSignal = signal;
    }
  });

  // Create summary
  symbolSummaries[symbol] = {
    symbol,
    overallBias,
    signalCounts,
    strongestSignal: strongestSignal ? {
      timeframe: strongestSignal.timeframe || 'unknown',
      signal: strongestSignal.signal || 'NEUTRAL',
      confidence: strongestSignal.confidence || 0,
      risk_reward: strongestSignal.risk_reward || 'N/A'
    } : null
  };
});

// Create market overview
const marketOverview = {
  timestamp: new Date().toISOString(),
  symbols: symbolSummaries,
  marketSummary: {
    bullishCount: Object.values(symbolSummaries).filter(s => s.overallBias === 'Bullish').length,
    bearishCount: Object.values(symbolSummaries).filter(s => s.overallBias === 'Bearish').length,
    neutralCount: Object.values(symbolSummaries).filter(s => s.overallBias === 'Neutral').length,
    overallMarketSentiment: Object.values(symbolSummaries).filter(s => s.overallBias === 'Bullish').length > 
                            Object.values(symbolSummaries).filter(s => s.overallBias === 'Bearish').length ? 
                            'Bullish' : 'Bearish'
  }
};

return {json: marketOverview};

